import Vue from 'vue';
import Vuex from 'vuex';

Vue.use (Vuex);

export default new Vuex.Store ({
  state: {
    users: [],
    loginUser: {},
  },
  mutations: {
    addUser (state, user) {
      state.users.push ({
        userName: user.userName,
        email: user.email,
        password: user.password,
        uid: state.users.length,
      });
      state.loginUser = user;
    },
  },
  actions: {},
  modules: {},
});
